import React, { useState } from 'react';
import axios from 'axios';

const InputForm = () => {
  const [symptoms, setSymptoms] = useState('');
  const [prediction, setPrediction] = useState(null);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/predict', null, {
        params: {
          symptoms: symptoms.trim().split(',').map(symptom => symptom.trim()).join(','),
        }
      });
      setPrediction(response.data);
    } catch (error) {
      setError('Error predicting disease.');
      console.error('Error predicting disease:', error);
    }
  };

  return (
    <div className="max-w-md mx-auto my-10 p-6 bg-white rounded-lg shadow-lg">
      <h2 className="text-2xl font-semibold mb-4">Disease Prediction</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <label className="block">
          <span className="text-gray-700">Symptoms (comma separated):</span>
          <input
            type="text"
            value={symptoms}
            onChange={(e) => setSymptoms(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-300 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"
            required
          />
        </label>
        <button
          type="submit"
          className="inline-block px-4 py-2 bg-indigo-500 text-white rounded-lg shadow-md hover:bg-indigo-600 focus:bg-indigo-600"
        >
          Predict
        </button>
      </form>

      {error && <p className="text-red-500 mt-2">{error}</p>}

      {prediction && (
        <div className="mt-6 bg-gray-100 p-4 rounded-lg shadow-md">
          <h3 className="text-xl font-semibold mb-2">Predicted Disease:</h3>
          <p className="text-lg text-indigo-600 mb-4">{prediction.predicted_disease}</p>

          <div className="border-t border-gray-200 pt-4">
            <h4 className="text-lg font-semibold mb-2">Description:</h4>
            <p className="text-gray-700">{prediction.description}</p>
          </div>

          <div className="border-t border-gray-200 pt-4">
            <h4 className="text-lg font-semibold mb-2">Precautions:</h4>
            <ul className="list-disc list-inside text-gray-700">
              {prediction.precautions.map((precaution, index) => (
                <li key={index}>{precaution}</li>
              ))}
            </ul>
          </div>

          <div className="border-t border-gray-200 pt-4">
            <h4 className="text-lg font-semibold mb-2">Recommended Diet:</h4>
            <ul className="list-disc list-inside text-gray-700">
              {prediction.recommended_diet.map((diet, index) => (
                <li key={index}>{diet}</li>
              ))}
            </ul>
          </div>

          <div className="border-t border-gray-200 pt-4">
            <h4 className="text-lg font-semibold mb-2">Workout:</h4>
            <p className="text-gray-700">{prediction.workout}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default InputForm;
