        </form>
        {predictedData && (
        <PredictedResults
            predictedData={predictedData}
            onReset={() => setPredictedData(null)}
        />
        )}
    </div>
    );

  
  return (
    <div>
      <form onSubmit={handleSubmit}>
        {/* Form input fields */}
