import React, { useState } from 'react';
import PredictedResults from './PredictedResults'; // Adjust the path as per your project structure

const SymptomInput = () => {
  const [predictedData, setPredictedData] = useState(null);
  const [error, setError] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const symptoms = event.target.elements.symptoms.value;

    try {
      const response = await fetch('http://localhost:8000/predict', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ symptoms }),
      });

      if (!response.ok) {
        throw new Error('Failed to fetch data');
      }

      const data = await response.json();
      setPredictedData(data);
      setError(null);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to predict disease. Please try again.');
      setPredictedData(null);
    }
  };

  const handleReset = () => {
    setPredictedData(null);
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Disease Prediction System</h1>
      <form onSubmit={handleSubmit} className="mb-4">
        <div className="flex items-center space-x-4">
          <input
            type="text"
            name="symptoms"
            placeholder="Enter symptoms (comma-separated)"
            className="border border-gray-300 px-2 py-1 rounded-lg w-96"
            required
          />
          <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded-lg">
            Predict
          </button>
        </div>
      </form>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      {predictedData && <PredictedResults predictedData={predictedData} onReset={handleReset} />}
    </div>
  );
};

export default SymptomInput;
